// ...existing code...
use CureConnect\Core\Request;
use CureConnect\Core\Response;
use CureConnect\models\Appointment;

class AppointmentController extends Controller
{
// ...existing code...
    public function create(Request $request, Response $response)
    {
        if ($request->isPost()) {
            $appointmentModel = new Appointment();
            $appointmentModel->loadData($request->getBody());
            if ($appointmentModel->validate() && $appointmentModel->save()) {
                $response->redirect('/appointments');
                return;
            }
            return $this->render('appointments/create', [
                'model' => $appointmentModel
            ]);
        }
        return $this->render('appointments/create', [
            'model' => new Appointment()
        ]);
    }

    public function update(Request $request, Response $response)
    {
        $id = $request->getRouteParam('id');
        $appointmentModel = (new Appointment())->findOne(['id' => $id]);

        if (!$appointmentModel) {
            // Handle not found case
            $response->setStatusCode(404);
            return $this->render('_404');
        }

        if ($request->isPost()) {
            $appointmentModel->loadData($request->getBody());
            if ($appointmentModel->validate() && $appointmentModel->update(['id' => $id])) {
                $response->redirect('/appointments');
                return;
            }
        }

        return $this->render('appointments/update', [
            'model' => $appointmentModel
        ]);
    }

    public function delete(Request $request, Response $response)
    {
        $id = $request->getRouteParam('id');
        $appointmentModel = new Appointment();
        if ($request->isPost()) {
            if ($appointmentModel->delete(['id' => $id])) {
                $response->redirect('/appointments');
                return;
            }
        }
        // Optional: render a confirmation page if it's a GET request
        // For now, redirecting or showing error might be enough.
        $response->redirect('/appointments');
    }
}

// ...existing code...
class Request
{
    private array $routeParams = [];

    public function getPath()
    {
// ...existing code...
    }

    public function method(): string
    {
        return strtolower($_SERVER['REQUEST_METHOD']);
    }

    public function isGet(): bool
    {
        return $this->method() === 'get';
    }

    public function isPost(): bool
    {
        return $this->method() === 'post';
    }

    public function getBody(): array
    {
// ...existing code...
        if ($this->method() === 'post') {
            foreach ($_POST as $key => $value) {
                $body[$key] = filter_input(INPUT_POST, $key, FILTER_SANITIZE_SPECIAL_CHARS);
            }
        }

        return $body;
    }

    public function getRouteParams(): array
    {
        return $this->routeParams;
    }

    public function setRouteParams(array $params): void
    {
        $this->routeParams = $params;
    }

    public function getRouteParam(string $param, $default = null)
    {
        return $this->routeParams[$param] ?? $default;
    }
}

// ...existing code...
    public function resolve()
    {
        $path = $this->request->getPath();
// ...existing code...
        $route = $this->routes[$method][$path] ?? false;

        if ($route === false) {
            foreach ($this->routes[$method] as $routePath => $callback) {
                if (preg_match_all('/\{(\w+)(:[^}]+)?}/', $routePath, $matches)) {
                    $pattern = preg_replace_callback('/\{\w+(:([^}]+))?}/', fn($m) => isset($m[2]) ? "({$m[2]})" : '(\w+)', $routePath);
                    if (preg_match("#^$pattern$#", $path, $routeMatches)) {
                        array_shift($routeMatches);
                        $params = array_combine($matches[1], $routeMatches);
                        $this->request->setRouteParams($params);
                        $route = $callback;
                        break;
                    }
                }
            }
        }

        if ($route === false) {
            $this->response->setStatusCode(404);
// ...existing code...
